mapper.insert:
insert into membercompanyfee (companyName, memberId, memberCompanyFeeAmount, memberCompanyFeePaid,
                              memberCompanyFeeCertificate, memberCompanyFeeChecked)
values (#{companyName}, #{memberId}, #{memberCompanyFeeAmount}, false, null, false);

mapper.update:
update company
set companyIntro = #{companyIntro}
where companyName = #{companyName};

update company
set companyFee = #{companyFee}
where companyName = #{companyName};

update company
set companyAlipay = #{companyAlipay}
where companyName = #{companyName};

mapper.select:
select *
from company
where companyName in (
    select membercompany.companyName
    from membercompany
    where memberId = #{memberId}
)
  and companyOwner != #{memberId}
