mapper.insert:
insert into managerapply (managerApplyCompanyName, managerApplyCompanyIntro, managerApplyCompanyAlipay,
                          memberId)
values (#{managerApplyCompanyName}, #{managerApplyCompanyIntro}, #{managerApplyCompanyAlipay}, #{memberId});

mapper.select:
select count(*)
from managerapply
where memberId = #{memberId};
