mapper.insert:
insert into membercompanyfee (companyName, memberId, memberCompanyFeeAmount, memberCompanyFeePaid,
                              memberCompanyFeeCertificate, memberCompanyFeeChecked)
values (#{companyName}, #{memberId}, #{memberCompanyFeeAmount}, false, null, false);

mapper.update:
update membercompanyfee
set memberCompanyFeeCertificate = #{memberCompanyCertificate}
    and memberCompanyFeePaid = true
where memberCompanyFeeId = #{memberCompanyFeeId}

update membercompanyfee
set memberCompanyFeeChecked = true
where memberCompanyFeeId = #{memberCompanyFeeId};

mapper.select:
select *
from membercompanyfee
where memberId = #{memberId}
  and memberCompanyFeePaid = false;

select *
from membercompanyfee
where companyName = #{companyName}
  and memberCompanyFeeChecked = false;
